package com.pickpic.Backend;import android.content.Context;import android.util.Log;import java.util.ArrayList;/** * Created by sekyo on 2017-05-11. */public class Synchronizer {    public static void synchronize(Context context) {        Log.v("test", "start synchronizer");        TagDBManager tagDBManager = new TagDBManager(context);        ArrayList<String> localImages = LocalImageManager.getAllImagePath(context, "ASC");        ArrayList<String> toBeErasedPath = tagDBManager.getToBeErasedPaths(localImages);        for(int i = 0; i<toBeErasedPath.size(); i++){            tagDBManager.removeImage(toBeErasedPath.get(i));        }        Log.v("test", "remove done");        tagDBManager.insertImagesIfNotExist(localImages);        Log.v("test", "insert done");        ArrayList<String> notAutoGeneratedPath = tagDBManager.getPathsNotAutoGenerated();        for(int i = 0; i<notAutoGeneratedPath.size(); i++){            AutoTagGenerator.autoTagGenerate(context, notAutoGeneratedPath.get(i));        }        Log.v("test", "sync done");    }}