package com.pickpic.Backend;import android.content.Context;import android.util.Log;import java.util.ArrayList;/** * Created by sekyo on 2017-05-11. */public class Syncronizer {    public static void synchronize(Context context) {        TagDBManager tagDBManager = new TagDBManager(context);        ArrayList<String> localImages = LocalImageManager.getAllImagePath(context);        ArrayList<String> TagDB = tagDBManager.getAllImages();        Log.v("test", "get all images");        int i = 0;        int j = 0;        while(j < TagDB.size() && i < localImages.size()){            if(localImages.get(i).equals(TagDB.get(j))){                i++;                j++;            }            else{                tagDBManager.removeImage(TagDB.get(j));                j++;            }        }        while(j < TagDB.size()){            tagDBManager.removeImage(TagDB.get(j));            j++;        }        while(i < localImages.size()){            AutoTagGenerator.autoTagGenerate(context, localImages.get(i));            i++;        }    }}